import React, { useEffect } from "react";
import {
  Card,
  CardBody,
  Col,
  Container,
  Row
} from "reactstrap";
import BootstrapTable from "react-bootstrap-table-next";
import paginationFactory from "react-bootstrap-table2-paginator";
import { connect } from "react-redux";
import { selectUsers } from "../../redux/selectors/userSelector";
import {getListUserAction} from "../../redux/actions/userActions";
import UserApi from "../../api/UserApi";
const tableColumns = [
  {
    dataField: "username",
    text: "User name",
    sort: true
  },
  {
    dataField: "email",
    text: "Email",
    sort: true
  },
  {
    dataField: "firstName",
    text: "Frist name",
    sort: true
  },
  {
    dataField: "lastName",
    text: "Last name",
    sort: true
  },
  {
    dataField: "role",
    text: "Role",
    sort: true
  }
];
const User = (props) => {
  const getListUser = props.getListUserAction;
  useEffect(() =>{
    const getAllUser = async() =>{
      const result = await UserApi.getAllUsers();
      const users = result.content;
      console.log(users);
      getListUser(users);
    }
    getAllUser();
  },[getListUser]);
  return(
  <Container fluid className="p-0">
    <h1 className="h3 mb-3">Admin Page</h1>
    <Row>
      <Col>
        <Card>
          <CardBody>
           <BootstrapTable
             keyField="name"
             data={props.users}
            columns={tableColumns}
            striped
            hover
            bootstrap4
            bordered={true}
            pagination={paginationFactory({
            sizePerPage: 5,
            sizePerPageList: [5, 10, 25, 50]
          })}
        />
        </CardBody>
        </Card>
      </Col>
    </Row>
  </Container>
)};
const mapGlobalStateToProps = state => {
  return {
    users: selectUsers(state),
    // page: selectPage(state),
    // size: selectSize(state),
    // totalElement: selectTotalElement(state),
    // sortField: selectSortField(state),
    // sortType: selectSortType(state),
    // selectedRows: selectSelectedRows(state)
  };
};
export default connect(mapGlobalStateToProps,{getListUserAction})(User);
