import React, { useEffect, useState } from "react";

import {
  Card,
  CardBody,
  CardHeader,
  CardTitle,
  Col,
  Container,
  Row
} from "reactstrap";
import { connect } from "react-redux";
import { selectFilms } from "../../redux/selectors/FilmSelector";
import paginationFactory from "react-bootstrap-table2-paginator";
import { getListFilmAction } from "../../redux/actions/FilmActions";
import '../../css/film.css'
import FilmApi from "../../api/FileApi"
const Film = (props) => {
  const getListFilm = props.getListFilmAction;
  useEffect(() =>{
    const getAll = async() =>{
      const result = await FilmApi.getAll();
      const films = result.content;
      
      getListFilm(films);
    }
    getAll(); 
  },[getListFilm]);

//  const data = props.films;
  return(
  <Container fluid className="p-0">
    <h1 className="h3 mb-3">Film Page</h1>
    <Row>
      <Col> 
        <Card>
          <CardHeader>
            <CardTitle tag="h5" className="mb-0">Danh sách phim</CardTitle>
          </CardHeader>
          <CardBody>
          <div className="category-products cgv-movies">
          <ul className="products-grid products-grid--max-4-col first last odd">
            <li className="film-lists item last">
              <div className="product-images">
                <a
                  href="https://www.cgv.vn/default/the-marvels.html"
                  title="THE MARVELS"
                  className="product-image"
                >
                  <img
                    id="product-collection-image-5416"
                    src="https://www.cgv.vn/media/catalog/product/cache/1/thumbnail/190x260/2e2b8cd282892c71872b9e67d2cb5039/r/s/rsz_56245l07_goat_vietnam_apac_poster_1sht.jpg"
                    alt="THE MARVELS"
                  />
                </a>
              </div>
              <div
                className="product-info"
                style={{ maxHeight: "none", height: 121, minHeight: 36 }}
              >
                <h2 className="product-name">
                  <a
                    href="https://www.cgv.vn/default/the-marvels.html"
                    title="THE MARVELS"
                  >
                    THE MARVELS
                  </a>
                </h2>
                <div className="cgv-movie-info">
                  <span className="cgv-info-bold">Thể loại: </span>
                  <span className="cgv-info-normal">
                    Hành Động, Phiêu Lưu, Thần thoại
                  </span>
                </div>
                <div className="cgv-movie-info">
                  <span className="cgv-info-bold">{getListFilm.duration} </span>
                  <span className="cgv-info-normal">104 phút</span>
                </div>
                <div className="cgv-movie-info">
                  <span className="cgv-info-bold">Khởi chiếu: </span>
                  <span className="cgv-info-normal">10-11-2023 </span>
                </div>
              </div>
            </li>
          </ul>
        </div>
        
          </CardBody>
        </Card>
      </Col>
    </Row>
  </Container>
)};
const mapGlobalStateToProps = state => {
  return {
    films: selectFilms(state),
    // page: selectPage(state),
    // size: selectSize(state),
    // totalSize: selectTotalSize(state),
    // selectedRows: selectSelectedRows(state),
  };
};
export default connect(mapGlobalStateToProps,{getListFilmAction})(Film);
