import * as types from "../constants";

const initialState = {
  users: [],
  page: 2,
  size: 7,
  totalSize: 0,

    // search
    search: null,

    // selected rows
    selectedRows: [],
};

export default function reducer(state = initialState, actions) {
  switch (actions.type) {
    case types.GET_LIST_USER:
      return {
        ...state,
        users: actions.payload.users,
        page: actions.payload.page,
        // size: actions.payload.size,
        totalSize: actions.payload.totalSize,
         // search
         search: actions.payload.search
      };

      case types.GET_LIST_USER_SELECTED_ROWS:
        return {
          ...state,
          selectedRows: actions.payload,
        };
    default:
      return state;
  }
}
